import React from "react";
import MySelect from "./UI/select/MySelect";
import MyInput from "./UI/input/MyInput";
import SearchButton from "./UI/button/SearchButton";
import Button from "@mui/material/Button";

const ClueFilter = ({ filter, setFilter }) => {
  return (
    <div>
      <MyInput
        placeholder="Поиск..."
        value={filter.query}
        onChange={(e) => setFilter({ ...filter, query: e.target.value })}
      />
      <div style={{ display: "flex", flexDirection: "row", gap: "9.5px" }}>
        <Button variant="outlined" sx={{ color: "#6580b2" }}>
          Advanced Search
        </Button>
        <SearchButton>Advanced Search</SearchButton>
        <SearchButton>Sort by...</SearchButton>
      </div>
      <div></div>
      {/* <MySelect
        value={filter.sort}
        onChange={(selectedSort) =>
          setFilter({ ...filter, sort: selectedSort })
        }
        defaultValue={"Сортировка..."}
        options={[
          { value: "title", name: "По названию" },
          { value: "body", name: "По описанию" },
        ]}
      /> */}
    </div>
  );
};

export default ClueFilter;
