import React, { useState, useEffect } from "react";
import ClueList from "./components/search/ClueList";
import { useFetchingClues } from "./components/hooks/useFetchingClues";
import ClueService from "./components/API/ClueService";
import Loader from "./components/UI/Loader/Loader";
import { useClues } from "./components/hooks/useClues";
import ClueFilter from "./components/ClueFilter";
import { useCategories } from "./components/hooks/useCategories";
import { useFetchingCategories } from "./components/hooks/useFetchingCategories";
import CategoryService from "./components/API/CategoryService";
import Header from "./components/Header.module/Header";

import "./App.css";

function App() {
  const [clues, setClues] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filter, setFilter] = useState({ sort: "question", query: "" });
  const [modalTab, setModalTab] = useState(true);

  const [fetchClues, isClueLoading, clueError] = useFetchingClues(async () => {
    const clues = await ClueService.getAll();
    setClues(clues);
  });

  const [fetchCategories, isCategoryLoading, categoryError] =
    useFetchingCategories(async () => {
      const categories = await CategoryService.getAll();
      setCategories(categories);
    });

  const sortedAndSearchedClues = useClues(clues, filter.sort, filter.query);

  const sortedAndSearchedCategories = useCategories(
    categories,
    filter.sort,
    filter.query
  );

  useEffect(() => {
    fetchClues();
  }, []);

  useEffect(() => {
    fetchCategories();
  }, []);

  // const [modal, setModal] = useState(false);

  return (
    <div className="App">
      {/* <MyModal visible={modal} setVisible={setModal}>
        <PostFrom create={createPost} />
      </MyModal> */}
      <Header></Header>

      <ClueFilter
        filter={filter}
        setFilter={setFilter}
        setModalTab={setModalTab}
        modalTab={modalTab}
      />
      {filter.sort === "questions" ? (
        <div>
          {isClueLoading ? (
            <div style={{ display: "flex", justifyContent: "center" }}>
              <Loader />
            </div>
          ) : (
            <ClueList
              clues={sortedAndSearchedClues}
              title={sortedAndSearchedClues.length + " Results found"}
            />
          )}
        </div>
      ) : (
        <div>
          {isCategoryLoading ? (
            <div style={{ display: "flex", justifyContent: "center" }}>
              <Loader />
            </div>
          ) : (
            <CategoryList
              categories={sortedAndSearchedCategories}
              title={sortedAndSearchedCategories.length + " Results found"}
            />
          )}
        </div>
      )}
    </div>
  );
}

export default App;
