import React, { useState, useEffect } from "react";
import ClueList from "./components/ClueList";
import { useFetchingClues } from "./components/hooks/useFetchingClues";
import ClueService from "./components/API/ClueService";
import Loader from "./components/UI/Loader/Loader";

import "./App.css";

function App() {
  const [clues, setClues] = useState([]);

  const [fetchClues, isClueLoading, clueError] = useFetchingClues(async () => {
    const clues = await ClueService.getAll();
    setClues(clues);
  });

  // const sortPosts = (sort) => {
  //   setFilter.sort(sort);
  // };

  useEffect(() => {
    fetchClues();
  }, []);

  // const [modal, setModal] = useState(false);

  return (
    <div className="App">
      {/* <MyModal visible={modal} setVisible={setModal}>
        <PostFrom create={createPost} />
      </MyModal> */}

      {/* <PostFilter filter={filter} setFilter={setFilter} /> */}

      {isClueLoading ? (
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Loader />
        </div>
      ) : (
        <ClueList clues={clues} title="Посты про JS" />
      )}
    </div>
  );
}

export default App;
